.app {
  font-family: "Roboto";
}
.app .title {
  font-size: 24px;
  padding: 8px;
  text-align: center;
  margin-top: 16px;
}
.app .items-container {
  padding: 24px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 8px;
}
.app .item {
  padding: 24px;
  background: white;
  display: flex;
  flex-direction: column;
  gap: 16px;
  position: relative;
  justify-content: center;
  align-items: center;
  width: 300px;
  border: 1px solid #222;
}
.app .item .image {
  display: block;
  max-width: 200px;
  max-height: 140px;
  min-height: 140px;
  width: auto;
  height: auto;
  position: relative;
}
.app .item .image img {
  max-height: 100%;
  max-width: 100%;
  width: auto;
  height: auto;
}
.app .item button {
  background: #222;
  color: white;
  border-radius: 8px;
  cursor: pointer;
  padding: 8px;
}
.app .item button:hover {
  opacity: 0.8;
}
.app .item .price {
  font-weight: 700;
  font-size: 24px;
  text-align: right;
}

