const MyCustomWidget = ({ data, label, triggerEventHandler }) => {
  const handleClick = (item) => {
    triggerEventHandler("onItemClick", { item });
  };

  return (
    <div>
      <h2>{label}</h2>
      <ul>
        {data.map((item, index) => (
          <li key={index} onClick={() => handleClick(item)}>
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyCustomWidget;
